import java.util.*;

public class Main {
    public static void main (String[] args) {
        Scanner sc = new Scanner(System.in);
        int N = sc.nextInt();
//        N = 3999;
        String S = sc.nextLine();
        S = sc.nextLine();
//        S = "";
        int[] Sint = new int[N];
        for (int i = 0; i < N; i++) {
            if (S.charAt(i) == 'R'){
                Sint[i] = 0;
            }
            if (S.charAt(i) == 'G'){
                Sint[i] = 1;
            }
            if (S.charAt(i) == 'B'){
                Sint[i] = 10;
            }
        }

//        HashMap<Integer, In> map = new HashMap<Integer>();
        long rCnt = 0;
        long gCnt = 0;
        long bCnt = 0;

        for (int i = 0; i < N; i++){
            if (Sint[i] == 0){
                rCnt++;
            } else if (Sint[i] == 1){
                gCnt++;
            } else {
                bCnt++;
            }
        }

        long ans = rCnt*gCnt*bCnt;
//        System.out.println("rCnt*gCnt*bCnt "+rCnt+" "+gCnt+" "+bCnt);
//        System.out.println("ans "+ans);
        int k = 0;
        for (int i = 0; i < N-2; i++){
            for (int j = i+1; j < N-1; j++){
                k = j + (j - i);
//                System.out.println(i+" "+j+" "+k);
                if (k >= N) {
                    continue;
                }
                int kind = Sint[i] + Sint[j] + Sint[k];
                if (kind == 11){
                    ans -= 1;
                }
            }
        }

//        System.out.println("ans "+ans);
        System.out.println(ans);
    }

}